import { Button, FormGroup, FormControl,FormLabel } from "react-bootstrap";
// import "./Login.css";


class Login extends Component {


    state = {
        redirect: false,
        email: "",
        password: ""
    }
    setRedirect = () => {

    }
    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect to='/homepage' />
        }
    }

    constructor(props) {
        super(props)
        this.state = {
            email:'',
            username: '',
            password: ''

        }

