import React,{Component} from 'react';
import {Redirect} from 'react-router-dom';
//import './login.css';
import axios from 'axios';
import Modal from 'react-awesome-modal';




class Userpage extends Component {



    constructor(props) {
        super(props);
        this.state = {

            visible : false,
            FirstName:'',
            LastName:'',
            MobileNo:'',
            Email:'',
            Place:'',
            Designation:''


        }
    }

    changeHandler= e=>{
        this.setState({[e.target.name]:e.target.value})
    }

    submitHandler= e=>{
    e.preventDefault()
    console.log(this.state)
    axios
    .post('http://......./authenticate',this.state)
    .then(response=> {

        console.log(response)

    })
    .catch(error=> {

        console.log(error)

    })

    }

       openModal() {
        this.setState({
            visible : true
        });
    }

    closeModal() {
        this.setState({
            visible : false
        });
    }


    render() { 
        const{FirstName,LastName,MobileNo,Email,Place,Designation}=this.state
        return (   
            <div>

         <div>

                 {/* {this.renderRedirect()}
                    <button type="submit" onClick={this.setRedirect} style={{backgroundColor:"#007bff"  }}>Add User</button> */}

            <header className="heading">

     <nav className="toolbar_navigation">

    <div className="toolbar_logo">

                  <img
                          src={require("/home/nineleaps/Desktop/onproject/onproject/src/Component/pages/user/nine1.jpg")}
                          width="150"
                          height="70"
                          className="d-inline-block align-top"
                          alt="React Bootstrap logo"
                          />
    </div>
            <div className="spacer"/>
            <div className="toolbar_navigation-items">
                <ul >
                <li><a href='/Homepage'>Checklist</a></li>
                 <li><a href='/Userpage'>Users</a></li>
                 <li><a href='./writeblog'>Blog</a></li>
                 <li><a href='./faqdis'>FAQ</a></li>
                 <li><a href='/'>Logout</a></li>
                </ul>
            </div>
    </nav>
     <section>

                <input type="button" value="Add user" onClick={() => this.openModal()} />
   <Modal visible={this.state.visible} width="800" height="600" effect="fadeInUp" onClickAway={() => this.closeModal()}>
                    <div>
                        {/* <h1>Title</h1>
                        <p>Some Contents</p> */}

         <form>
            <br/>
                <div class="form-group">
                    <label for="formGroupExampleInput">FirstName</label>
                    <input type="text" name ="FirstName" value={FirstName} class="form-control" onChange={this.changeHandler} placeholder="Example input"/>
                </div>
                <div class="form-group">
                    <label for="formGroupExampleInput2">LastName</label>
                    <input type="text" name="LastName" value={LastName} class="form-control" onChange={this.changeHandler} placeholder="Another input"/>
                 </div>
                 <div class="form-group">
                    <label for="formGroupExampleInput2">MobileNo.</label>
                    <input type="text" name="MobileNo." value={MobileNo }  onChange={this.changeHandler} class="form-control"  placeholder="Another input"/>
                 </div>

                 <div class="form-group">
                    <label for="formGroupExampleInput2">Email</label>
                    <input type="text"name ="Email" value={Email}  onChange={this.changeHandler}class="form-control"  placeholder="Another input"/>
                 </div>
                 <div class="form-group">
                    <label for="formGroupExampleInput2">Place</label>
                    <input type="text"  name="Place" value={Place}  onChange={this.changeHandler} class="form-control"  placeholder="Another input"/>
                 </div>
                 <div class="form-group">
                    <label for="formGroupExampleInput2">Designation</label>
                    <input type="text" name ="Designation" value={Designation} onChange={this.changeHandler} class="form-control"  placeholder="Another input"/>
                 </div>
          </form>
                   {/* <a href="javascript:void(0);" onClick={() => this.closeModal()}>Close</a>
                   <a href="javascript:void(0);" onClick={() => this.closeModal()}>Save</a> */}

                        {this.renderRedirect()}
                    <input type="submit"  value="Login" style={{backgroundColor:"#007bff"  }}/>


                    </div>
                </Modal>
            </section> 
</header>




                              <div class="footer">
                                        <p>Nineleaps</p>
                                    </div>
            </div>
</div>

        );
    }
}

export default Userpage;
